CREATE TABLE "public"."dpfi" ("id" serial NOT NULL, "code_postal" varchar, "ville" varchar, "telephone" varchar, "location_adresse" varchar, "telephone_portable" varchar, "created_at" timestamptz DEFAULT CURRENT_TIMESTAMP, "email_send" timestamptz, "sirect" varchar, "latitude" float4, "longitude" float4, "departement_code" varchar, "suspend_activity" boolean, "suspend_activity_reason" text, "adresse" text, "adresse_complement" text, "user_id" integer, "editor_id" integer, "liste_blache_id" integer, PRIMARY KEY ("id") , FOREIGN KEY ("departement_code") REFERENCES "public"."departements"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("editor_id") REFERENCES "public"."editors"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("liste_blache_id") REFERENCES "public"."liste_blanche"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("user_id") REFERENCES "public"."users"("id") ON UPDATE cascade ON DELETE cascade, UNIQUE ("sirect"), UNIQUE ("liste_blache_id"), UNIQUE ("user_id"));
